
// Clock with large digits using the "Anton" bold font
Graphics.prototype.setFontAnton = function (scale) {
    // Actual height 69 (68 - 0)
    g.setFontCustom(atob(""), 46, atob("EiAnGicnJycnJycnEw=="), 78 + (scale << 8) + (1 << 16));
};

{
    const Storage = require("Storage");

    // === إعدادات عامة ===
    const YEAR = 2025; // عدّلها للسنة المطلوبة
    const CITY = "JEDDAH"; // ضع رمز مدينتك: "makkah", "jeddah", "madinah", ...

    let yearFile = function (year) { // 1..12
        return "uaq-" + CITY.toLowerCase() + "-" + year + ".json";
    };

    // يعيد {1:[hh:mm*6],2:[...],...} أو null
    let loadMonth = function (year) {
        const fn = yearFile(year);
        try {
            const obj = Storage.readJSON(fn);
            return obj || null;
        } catch (e) {
            return null;
        }
    };

    let todayYMD = function (d) {
        return { y: d.getFullYear(), m: d.getMonth() + 1, day: d.getDate() };
    };

    let getTimesFor = function (d) {
        const ymd = todayYMD(d);
        let data = loadMonth(ymd.y);
        if (!data) return null;
        let now = new Date();
        let start = new Date(now.getFullYear(), 0, 0);
        let diff = now - start;
        let oneDay = 1000 * 60 * 60 * 24;
        ymd.day = Math.floor(diff / oneDay);
        let arr = data[ymd.day];
        if (!arr) return null;
        // arr = ["05:34","06:54","12:19","15:20","17:42","19:12"]
        return {
            fajr: arr[0], sunrise: arr[1], dhuhr: arr[2],
            asr: arr[3], maghrib: arr[4], isha: arr[5],
            hDay: arr[6], hMonth: arr[7], hYear: arr[8]
        };
    };

    let parseHM = function (s) {
        // "HH:MM" -> minutes from midnight
        const a = s.split(":"); return (+a[0]) * 60 + (+a[1]);
    };

    let nextPrayerLabel = function (nowMin, t) {
        const order = [
            ["Fajr", parseHM(t.fajr)],
            ["Sunrise", parseHM(t.sunrise)],
            ["Dhuhr", parseHM(t.dhuhr)],
            ["Asr", parseHM(t.asr) + 720],
            ["Maghrib", parseHM(t.maghrib) + 720],
            ["Isha", parseHM(t.isha) + 720]
        ];
        for (let i = 0; i < order.length; i++) {
            if (order[i][1] >= nowMin) return order[i];
        }
        // إنتهى اليوم: القادمة فجر الغد (للعرض فقط)
        return ["Fajr", parseHM(t.fajr) + 1440];
    };

    // ==== الرسم لواجهة Bangle.js 2 (176x176) ====
    let shownDate = new Date(); // يمكن التنقل باليوم للأمام/الخلف
    let drawTimeout;
    let draw = function () {
        var x = g.getWidth() / 2;
        var y = g.getHeight() / 2;
        g.reset().clearRect(Bangle.appRect); // clear whole background (w/o widgets)
        var date = new Date();
        var hours = date.getHours();
        var minutes = date.getMinutes();
        var timeStr = (" " + (hours % 12 || 12)).substr(-2) + ":" + ("0" + minutes).substr(-2); // Hour and minute
        g.setFontAlign(0, 0).setFont("Anton").drawString(timeStr, x, y + 17);

        // var dateStr = require("locale").date(date, 0).toUpperCase()+"\n"+
        //               require("locale").dow(date, 0).toUpperCase();
        // g.setFontAlign(0, 0).setFont("6x8", 2).drawString(dateStr, x, y+48);

        g.setFont("6x8", 2);
        g.setFontAlign(0, -1);

        const t = getTimesFor(shownDate);
        const title = CITY;
        g.drawString(title, g.getWidth() / 2, 6);

        const ymd = todayYMD(shownDate);
        g.setFont("6x8", 1);
        g.drawString(require("locale").date(date, 0).toUpperCase(), g.getWidth() / 2, 25);
        g.drawString(require("locale").dow(date, 0).substr(0, 3).toUpperCase() + "\t" + t.hYear + "-" + t.hMonth + "-" + ("0" + t.hDay).substr(-2), g.getWidth() / 2, 37);

        if (!t) {
            g.setFont("6x8", 2);
            g.drawString("Month data missing!", g.getWidth() / 2, 60);
            g.setFont("6x8", 1);
            g.drawString("Install file:", g.getWidth() / 2, 80);
            g.drawString(yearFile(ymd.y), g.getWidth() / 2, 92);
            g.drawString("via IDE: Storage.writeJSON(...)", g.getWidth() / 2, 110);
            return;
        }

        // القائمة
        g.setFont("6x8", 2);
        y = 36;
        const rows = [
            ["Fajr", t.fajr],
            ["Sunrise", t.sunrise],
            ["Dhuhr", t.dhuhr],
            ["Asr", t.asr],
            ["Maghrib", t.maghrib],
            ["Isha", t.isha]
        ];
        // for (let i=0;i<rows.length;i++){
        //   g.setFontAlign(-1,-1); g.drawString(rows[i][0], 8, y);
        //   g.setFontAlign(1,-1); g.drawString(rows[i][1], g.getWidth()-8, y);
        //   y += 20;
        // }

        // الصلاة القادمة
        const nowMin = hours * 60 + minutes;
        const np = nextPrayerLabel(nowMin, t);
        const minsLeft = (np[1] - nowMin + 1440) % 1440;

        g.setFontAlign(0, -1);
        g.drawLine(5, y + 15, g.getWidth() - 5, y + 15);
        g.drawString(np[0] + " in", g.getWidth() / 2, y + 105);
        g.drawString(("0" + Math.floor(minsLeft / 60)).substr(-2) + ":" + ("0" + (minsLeft % 60)).substr(-2), g.getWidth() / 2, y + 121);

        // queue next draw
        if (drawTimeout) clearTimeout(drawTimeout);
        drawTimeout = setTimeout(function () {
            drawTimeout = undefined;
            draw();
        }, 60000 - (Date.now() % 60000));
    };

    // Show launcher when middle button pressed
    Bangle.setUI({
        mode: "clock",
        remove: function () {
            // Called to unload all of the clock app
            if (drawTimeout) clearTimeout(drawTimeout);
            drawTimeout = undefined;
            delete Graphics.prototype.setFontAnton;
        },
        redraw: draw,
    });
    // Load widgets
    Bangle.loadWidgets();
    draw();
    setTimeout(Bangle.drawWidgets, 0);
}
