const SETTINGS_FILE = "bwclk.setting.json";
const locale = require('locale');
const storage = require('Storage');


/*
 * Load settings
 */
let settings = {
  fullscreen: false,
  showLock: true,
};

let saved_settings = storage.readJSON(SETTINGS_FILE, 1) || settings;
for (const key in saved_settings) {
  settings[key] = saved_settings[key]
}


/*
 * Assets
 */

// Manrope font
Graphics.prototype.setLargeFont = function(scale) {
  // Actual height 41 (42 - 2)
  this.setFontCustom(atob(""), 46, atob("DxcjFyAfISAiHCAiEg=="), 54+(scale<<8)+(1<<16));
  return this;
};

Graphics.prototype.setSmallFont = function(scale) {
  // Actual height 28 (27 - 0)
  this.setFontCustom(atob(""), 32, atob("BgkMGhEZEgYMDAwQCAwICxILEBAREBEOEREJCREVEQ8ZEhEUExAOFBQHDREPGBMUERQSEhEUERsREBIMCwwTEg4QERAREQoREQcHDgcYEREREQoPDBEPFg8PDwwIDBMc"), 28+(scale<<8)+(1<<16));
  return this;
};

var imgLock = {
  width : 16, height : 16, bpp : 1,
  transparent : 0,
  buffer : E.toArrayBuffer(atob("A8AH4A5wDDAYGBgYP/w//D/8Pnw+fD58Pnw//D/8P/w="))
};


// timeout used to update every minute
var W = g.getWidth();
var H = g.getHeight();
var drawTimeout;

// schedule a draw for the next minute
function queueDraw() {
  if (drawTimeout) clearTimeout(drawTimeout);
  drawTimeout = setTimeout(function() {
    drawTimeout = undefined;
    draw();
  }, 60000 - (Date.now() % 60000));
}


/*
 * Handle alarm
 */
function getSteps() {
  try{
      if (WIDGETS.wpedom !== undefined) {
          return WIDGETS.wpedom.getSteps();
      } else if (WIDGETS.activepedom !== undefined) {
          return WIDGETS.activepedom.getSteps();
      } else {
        return Bangle.getHealthStatus("day").steps;
      }
  } catch(ex) {
      // In case we failed, we can only show 0 steps.
  }

  return 0;
}


function isAlarmEnabled(){
  try{
    var qalarm = require('qalarm');
    return qalarm.isTimerStarted("bwclk");
  } catch(ex){ }
  return false;
}


function getAlarmMinutes(){
  try{
    var qalarm = require('qalarm');
    return qalarm.getTimerMin("bwclk");
  } catch(ex){ }
  return -1;
}

function increaseAlarm(){
  try{
    var qalarm = require('qalarm');
    var mins = qalarm.getTimerMin("bwclk")+5;
    qalarm.deleteTimer("bwclk");
    qalarm.editTimer("bwclk", 0, mins, 0);
  } catch(ex){ }
}

function decreaseAlarm(){
  try{
    var qalarm = require('qalarm');
    var mins = qalarm.getTimerMin("bwclk")-5;
    qalarm.deleteTimer("bwclk");
    if(mins > 0){
        qalarm.editTimer("bwclk", 0, mins, 0);
    }
  } catch(ex){ }
}


function draw() {
  // queue draw in one minute
  queueDraw();

  // Draw background
  var yOffset = settings.fullscreen ? 0 : 10;
  var y = H/5*2 + yOffset;
  g.reset().clearRect(0,0,W,W);
  g.setColor("#000");
  g.fillRect(0,y,W,H);

  // Draw date
  var date = new Date();
  g.setColor("#000");
  g.setFontAlign(0,1);
  g.setLargeFont();
  var dateStr = date.getDate();
  g.drawString(dateStr, W/2, y+5);
  var strW = g.stringWidth(dateStr);

  g.setSmallFont();
  g.setFontAlign(1,1);
  g.drawString(locale.dow(date, true), W/2-strW/2, y);

  g.setFontAlign(-1,1);
  var monthStr = locale.month(date, 1);
  g.drawString(monthStr, W/2+strW/2, y);

  // Draw time
  g.setColor("#fff");
  g.setLargeFont();
  g.setFontAlign(0,-1);
  var timeStr = locale.time(date,1);
  g.drawString(timeStr, W/2, y+10);

  // Draw steps or timer
  y += H/5*2+10;
  g.setSmallFont();
  g.setFontAlign(0,0);
  var str = isAlarmEnabled() ? "T-" + getAlarmMinutes() + " min." : getSteps() ;
  g.drawString(str, W/2, y);


  // Draw lock
  if(settings.showLock && Bangle.isLocked()){
    g.setColor("#000");
    g.drawImage(imgLock, 2, 2);
  }

  // Draw widgets if not fullscreen
  if(settings.fullscreen){
    for (let wd of WIDGETS) {wd.draw=()=>{};wd.area="";}
  } else {
    Bangle.drawWidgets();
  }
}

Bangle.loadWidgets();

// Clear the screen once, at startup
g.setTheme({bg:"#fff",fg:"#000",dark:false}).clear();
// draw immediately at first, queue update
draw();


// Stop updates when LCD is off, restart when on
Bangle.on('lcdPower',on=>{
  if (on) {
    draw(); // draw immediately, queue redraw
  } else { // stop draw timer
    if (drawTimeout) clearTimeout(drawTimeout);
    drawTimeout = undefined;
  }
});


Bangle.on('lock', function(isLocked) {
  if (drawTimeout) clearTimeout(drawTimeout);
  drawTimeout = undefined;
  draw();
});


Bangle.on('touch', function(btn, e){
  var upper = parseInt(g.getHeight() * 0.2);
  var lower = g.getHeight() - upper;

  var is_upper = e.y < upper;
  var is_lower = e.y > lower;

  if(is_upper){
    Bangle.buzz(40, 0.6);
    increaseAlarm();
    draw(true);
  }

  if(is_lower){
    Bangle.buzz(40, 0.6);
    decreaseAlarm();
    draw(true);
  }
});


// Show launcher when middle button pressed
Bangle.setUI("clock");
